import React, { useEffect, useState } from 'react';
import axios from 'axios';

function Dashboard() {
  const [docs, setDocs] = useState([]);
  const [title, setTitle] = useState('');
  const [content, setContent] = useState('');
  const token = localStorage.getItem('token');

  // ‚úÖ Fetch all documents
  const fetchDocs = async () => {
    try {
      const res = await axios.get('/api/docs', {
        headers: {
          Authorization: token
        }
      });
      setDocs(res.data);
    } catch (err) {
      alert('‚ö†Ô∏è Error fetching documents. Please log in again.');
    }
  };

  // ‚úÖ Run on page load
  useEffect(() => {
    fetchDocs();
  }, []);

  // ‚úÖ Create new document
  const createDoc = async () => {
    if (!title || !content) return alert("Please enter title and content");

    try {
      await axios.post('/api/docs', { title, content }, {
        headers: {
          Authorization: token
        }
      });
      setTitle('');
      setContent('');
      fetchDocs(); // refresh
    } catch (err) {
      alert('‚ö†Ô∏è Error creating document.');
    }
  };
  const handleLogout = () => {
    localStorage.removeItem('token');
    window.location.href = '/login'; // force redirect
  };
  

  // ‚úÖ Delete document
  const deleteDoc = async (id) => {
    try {
      await axios.delete(`/api/docs/${id}`, {
        headers: {
          Authorization: token
        }
      });
      fetchDocs(); // refresh
    } catch (err) {
      alert('‚ö†Ô∏è Error deleting document.');
    }
  };

  return (
    <div style={{ padding: "2rem" }}>
      <h2>üìÑ My Documents</h2>

      <input
        placeholder="Title"
        value={title}
        onChange={e => setTitle(e.target.value)}
      /><br /><br />

      <textarea
        placeholder="Content"
        value={content}
        onChange={e => setContent(e.target.value)}
        rows={5}
        cols={40}
      /><br /><br />

      <button onClick={createDoc}>‚ûï Create Document</button>

      <hr />

      <ul>
        {docs.map(doc => (
          <li key={doc._id}>
            <h3>{doc.title}</h3>
            <p>{doc.content}</p>
            <button onClick={() => deleteDoc(doc._id)}>üóë Delete</button>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default Dashboard;
